mapper.select:
SELECT A.*,
IFNULL(SUM(RP.point),0) AS extra__sumReactionPoint, IFNULL(SUM(IF(RP.point > 0, RP.point, 0)),0) AS extra__goodReactionPoint, IFNULL(SUM(IF(RP.point < 0, RP.point, 0)),0) AS extra__badReactionPoint FROM ( SELECT A.*, M.nickname AS extra__writerName FROM article AS A LEFT JOIN `member` AS M ON A.memberId = M.id WHERE 1
AND A.boardId = #{boardId}
AND A.title LIKE CONCAT('%',#{searchKeyword},'%')
AND A.body LIKE CONCAT('%',#{searchKeyword},'%')
AND ( A.title LIKE CONCAT('%',#{searchKeyword},'%') OR A.body LIKE CONCAT('%',#{searchKeyword},'%') )
LIMIT #{limitStart}, #{limitTake}
) AS A LEFT JOIN reactionPoint AS RP ON RP.relTypeCode = 'article' AND A.id = RP.relId GROUP BY A.id;
